88;s:72:"INSERT INTO rtpd_options VALUES( '75', 'thread_comments', '1', 'yes' ); ";i:89;s:78:"INSERT INTO rtpd_options VALUES( '76', 'thread_comments_depth', '5', 'yes' ); ";i:90;s:69:"INSERT INTO rtpd_options VALUES( '77', 'page_comments', '', 'yes' ); ";i:91;s:75:"INSERT INTO rtpd_options VALUES( '78', 'comments_per_page', '50', 'yes' ); ";i:92;s:83:"INSERT INTO rtpd_options VALUES( '79', 'default_comments_page', 'newest', 'yes' ); ";i:93;s:72:"INSERT INTO rtpd_options VALUES( '80', 'comment_order', 'asc', 'yes' ); ";i:94;s:94:"INSERT INTO rtpd_options VALUES( '81', 'sticky_posts', 'a:2:{i:0;i:142;i:1;i:138;}', 'yes' ); ";i:95;s:105:"INSERT INTO rtpd_options VALUES( '82', 'widget_categories', 'a:1:{s:12:\"_multiwidget\";i:1;}', 'yes' ); ";i:96;s:5206:"INSERT INTO rtpd_options VALUES( '83', 'widget_text', 'a:7:{i:2;a:3:{s:5:\"title\";s:43:\"Ортопедические стельки\";s:4:\"text\";s:280:\"Индивидуальные ортопедические термопластичные стельки от компании Sursil-Ortho в Стаханове по адресу ул.&nbsp;Пономарчука, 13, поликлиника №&nbsp;1, кабинет №&nbsp;12.\";s:6:\"filter\";b:0;}i:3;a:3:{s:5:\"title\";s:16:\"Контакты\";s:4:\"text\";s:101:\"Телефон: +380 95 505-85-47\r\nE-mail: <a href=\"mailto:a.hmarenko@mail.ru\">a.hmarenko@mail.ru</a>\";s:6:\"filter\";b:1;}i:4;a:3:{s:5:\"title\";s:22:\"Рекомендуем\";s:4:\"text\";s:646:\"<ul>\r\n<li><a href=\"http://medortoped.lg.ua/\" target=\"_blank\">Медортопед — Протезная мастерская</a></li>\r\n<li><a href=\"http://med-zentrum.com/\" target=\"_blank\" title=\"Компания Med Zentrum Wuppertal — организация обследования, лечения, реабилитации в Германии\">Лечение в Германии</a></li>\r\n<li><a href=\"http://meddus.com/\" target=\"_blank\" title=\"Обследование, лечение и реабилитация в Германии\">Компания MedDus — медицинский туризм в&nbsp;Германии</a></li>\r\n</ul>\";s:6:\"filter\";b:0;}i:5;a:3:{s:5:\"title\";s:22:\"Что мы лечим\";s:4:\"text\";s:2076:\"Продольное и поперечное <a title=\"Новая методика изготовления стелек\" href=\"/novaya-metodika-izgotovleniya-stelek.html\" style=\"text-decoration: underline;\">плоскостопие</a>, травмы и деформации стоп (<a title=\"«Косточки» в области стопы или Hallux Valgus\" href=\"/kostochki-v-oblasti-stopy-ili-hallux-valgus.html\" style=\"text-decoration: underline;\">hallux valgus</a> — «косточки», <a title=\"О мозолях, натоптышах, «молотках» и малоинвазивной хирургии стоп\" href=\"/o-mozolyax-natoptyshax-molotkax-i-maloinvazivnoj-xirurgii-stop.html\" style=\"text-decoration: underline;\">молоткообразнные</a> деформации пальцев, <a title=\"О мозолях, натоптышах, «молотках» и малоинвазивной хирургии стоп\" href=\"/o-mozolyax-natoptyshax-molotkax-i-maloinvazivnoj-xirurgii-stop.html\" style=\"text-decoration: underline;\">натоптыши</a>, пальцевые <a title=\"О мозолях, натоптышах, «молотках» и малоинвазивной хирургии стоп\" href=\"/o-mozolyax-natoptyshax-molotkax-i-maloinvazivnoj-xirurgii-stop.html\" style=\"text-decoration: underline;\">мозоли</a>), травмы и <a title=\"Травмы и заболевания кисти\" href=\"/travmy-i-zabolevaniya-kisti.html\" style=\"text-decoration: underline;\">заболевания кисти</a>, <a title=\"Пяточная шпора (плантарный фасциит)\" href=\"/pyatochnaya-shpora-plantarnyj-fasciit.html\" style=\"text-decoration: underline;\">пяточную шпору</a>, <a title=\"Ударно-волновая терапия\" href=\"/udarno-volnovaya-terapiya.html\" style=\"text-decoration: underline;\">артрозы</a>.\r\n\r\nПрием ведет врач травматолог-ортопед высшей категории Хмаренко Александр Николаевич.\";s:6:\"filter\";b:1;}i:6;a:3:{s:5:\"title\";s:0:\"\";s:4:\"text\";s:1173:\"			<div class=\"widget\">\r\n				<h4>Принимает</h4>\r\n				<p>Врач травматолог-ортопед высшей категории Хмаренко Александр Николаевич</p>\r\n			</div>\r\n\r\n			<div class=\"widget\">\r\n				<h4>Контакты</h4>\r\n\r\n				<ul class=\"contact-informations\">\r\n					<li><span class=\"address\">94016 Украина, Луганская обл., г.&nbsp;Стаханов, ул.&nbsp;Пономарчука, 13, поликлиника №&nbsp;1, кабинет №&nbsp;12</span></li>\r\n				</ul>\r\n\r\n				<ul class=\"contact-informations second\">\r\n					<li><i class=\"halflings phone\"></i> <p>+380 95 505-85-47</p></li>\r\n					<li><i class=\"halflings envelope\"></i> <p>a.hmarenko@mail.ru</p></li>\r\n					<li><i class=\"halflings globe\"></i> <p>ortoped.lg.ua</p></li>\r\n				</ul>\r\n\r\n			</div>\r\n\r\n			<div class=\"widget\">\r\n				<h4>Время работы</h4>\r\n				<ul class=\"contact-informations hours\">\r\n					<li><i class=\"halflings time\"></i>Пн-Пт <span class=\"hours\">с 9:00 до 13:00</span></li>\r\n					<li><i class=\"halflings ban-circle\"></i>Сб-Вс <span class=\"hours\">выходной</span></li>\r\n				</ul>\r\n			</div>\";s:6:\"filter\";b:0;}i:7;a:3:{s:5:\"title\";s:0:\"\";s:4:\"text\";s:106:\"Пусть движение сделает вашу жизнь свободной и интересной!\";s:6:\"filter\";b:0;}s:12:\"_multiwidget\";i:1;}', 'yes' ); ";i:97;s:72:"INSERT INTO rtpd_options VALUES( '84', 'widget_rss', 'a:0:{}', 'yes' ); ";i:98;s:246:"INSERT INTO rtpd_options VALUES( '85', 'uninstall_plugins', 'a:2:{s:27:\"wp-pagenavi/wp-pagenavi.php\";s:14:\"__return_false\";s:41:\"better-wp-security/better-wp-security.php\";a:2:{i:0;s:10:\"bwps_setup\";i:1;s:12:\"on_uninstall\";}}', 'no' ); ";i:99;s:71:"INSERT INTO rtpd_options VALUES( '86', 'timezone_string', '', 'yes' ); ";i:100;s:71:"INSERT INTO rtpd_options VALUES( '87', 'page_for_posts', '0', 'yes' ); ";i:101;s:71:"INSERT INTO rtpd_options VALUES( '88', 'page_on_front', '76', 'yes' ); ";i:102;s:76:"INSERT INTO rtpd_options VALUES( '89', 'default_post_format', '0', 'yes' ); ";i:103;s:77:"INSERT INTO rtpd_options VALUES( '90', 'link_manager_enabled', '0', 'yes' ); ";i:104;s:79:"INSERT INTO rtpd_options VALUES( '91', 'initial_db_version', '26691', 'yes' ); ";i:105;s:3490:"INSERT INTO rtpd_options VALUES( '92', 'rtpd_user_roles', 'a:5:{s:13:\"administrator\";a:2:{s:4:\"name\";s:13:\"Administrator\";s:12:\"capabilities\";a:62:{s:13:\"switch_themes\";b:1;s:11:\"edit_themes\";b:1;s:16:\"activate_plugins\";b:1;s:12:\"edit_plugins\";