ecent_blog limit=\"4\" title=\"Последние статьи\" subtitle=\"\" words=\"15\" ]', 'Врач травматолог-ортопед высшей категории', '', 'inherit', 'closed', 'closed', '', '76-revision-v1', '', '', '2014-02-16 18:51:54', '2014-02-16 16:51:54', '', '76', 'http://384071.ortope00.web.hosting-test.net/76-revision-v1.html', '0', 'revision', '', '0' ); ";i:794;s:421:"INSERT INTO rtpd_posts VALUES( '88', '2', '2014-02-16 18:56:50', '2014-02-16 16:56:50', '[gallery link=\"file\" ids=\"293,292,291,290,289,288,287,286,285\" orderby=\"DESC\"]', 'Дипломы и сертификаты', '', 'publish', 'closed', 'closed', '', 'certificates', '', '', '2014-02-27 10:34:17', '2014-02-27 08:34:17', '', '0', 'http://384071.ortope00.web.hosting-test.net/?page_id=88', '0', 'page', '', '0' ); ";i:795;s:352:"INSERT INTO rtpd_posts VALUES( '89', '2', '2014-02-16 18:56:50', '2014-02-16 16:56:50', '', 'Дипломы и сертификаты', '', 'inherit', 'closed', 'closed', '', '88-revision-v1', '', '', '2014-02-16 18:56:50', '2014-02-16 16:56:50', '', '88', 'http://384071.ortope00.web.hosting-test.net/88-revision-v1.html', '0', 'revision', '', '0' ); ";i:796;s:997:"INSERT INTO rtpd_posts VALUES( '90', '2', '2014-02-16 18:57:26', '2014-02-16 16:57:26', '<h3>Мы находимся</h3>\r\n<script charset=\"utf-8\" type=\"text/javascript\" src=\"//api-maps.yandex.ru/services/constructor/1.0/js/?sid=kVeVmImiBKbUx6g33Qe1sMHu9fJGbb7L&amp;width=640&amp;height=250\"></script>\r\n<h3>Напишите нам</h3>\r\nЕсли у вас возникли вопросы, замечания или предложения, отправьте их нам, заполнив приведенную ниже форму. Мы обязательно ответим вам по указанному адресу электронной почты.\r\n\r\n[contact-form-7 id=\"4\" title=\"Обратная связь\"]\r\n\r\n&nbsp;', 'Контактная информация', '', 'publish', 'closed', 'closed', '', 'contacts', '', '', '2014-02-27 11:00:09', '2014-02-27 09:00:09', '', '0', 'http://384071.ortope00.web.hosting-test.net/?page_id=90', '0', 'page', '', '0' ); ";i:797;s:353:"INSERT INTO rtpd_posts VALUES( '91', '2', '2014-02-16 18:57:26', '2014-02-16 16:57:26', '', 'Контактная информация', '', 'inherit', 'closed', 'closed', '', '90-revision-v1', '', '', '2014-02-16 18:57:26', '2014-02-16 16:57:26', '', '90', 'http://384071.ortope00.web.hosting-test.net/90-revision-v1.html', '0', 'revision', '', '0' ); ";i:798;s:306:"INSERT INTO rtpd_posts VALUES( '92', '2', '2014-02-16 19:00:55', '2014-02-16 17:00:55', '', 'Контакты', '', 'publish', 'closed', 'closed', '', '92', '', '', '2014-02-21 11:49:17', '2014-02-21 09:49:17', '', '0', 'http://384071.ortope00.web.hosting-test.net/?p=92', '6', 'nav_menu_item', '', '0' ); ";i:799;s:291:"INSERT INTO rtpd_posts VALUES( '93', '2', '2014-02-16 19:00:55', '2014-02-16 17:00:55', ' ', '', '', 'publish', 'closed', 'closed', '', '93', '', '', '2014-02-21 11:49:17', '2014-02-21 09:49:17', '', '0', 'http://384071.ortope00.web.hosting-test.net/?p=93', '3', 'nav_menu_item', '', '0' ); ";i:800;s:3998:"INSERT INTO rtpd_posts VALUES( '94', '2', '2014-02-19 08:02:11', '2014-02-19 06:02:11', '<h3><strong>Что мы лечим</strong></h3>\r\n<ul class=\"check-list\">\r\n	<li>Заболевания крупных суставов (тазобедренный, коленный, плечевой локтевой, лучезапясный, голеностопный) и мелких (кисть и стопа) — в том числе «теннисный локоть», «рука гольфиста»</li>\r\n	<li>Пяточную шпору и плантарный фасциит</li>\r\n	<li>Заболевания связок (тендопериостопатия и т. п.)</li>\r\n	<li>Мышечно-тонические боли при остеохондрозе позвоночника, кокцикодинию</li>\r\n	<li>Мускульные болевые (триггерные) точки</li>\r\n	<li>Замедленную консолидацию длинных трубчатых костей</li>\r\n</ul>\r\n&nbsp;\r\n<h3><strong>Принцип действия ударно-волновой терапии</strong></h3>\r\nУдарно-волновая терапия проводится на специальном аппарате — генераторе тока. Электрический импульс, подаваемый прибором, преобразуется в звуковую волну и проникает в тело человека. Встречая на своем пути ткани различной плотности, акустическая волна с частотой 1–10 Гц распространяется в теле человека и поглощается, достигнув кости. Энергии ударно-волнового воздействия достаточно для оздоровления фиброзных участков, костных разрастаний и зон отложения солей. Кроме того, создаются условия для более быстрого срастания костей при переломах.\r\n<h3><strong>Преимущества метода</strong></h3>\r\n<ul class=\"check-list\">\r\n	<li>УВТ хорошо переносится и не требует обезболивания</li>\r\n	<li>Проводится в амбулаторных условиях</li>\r\n	<li>Во многих случаях позволяет избежать проведения оперативного вмешательства</li>\r\n</ul>\r\n&nbsp;\r\n<h3><strong>Противопоказания</strong></h3>\r\n<ul class=\"check-list\">\r\n	<li>Онкологические заболевания</li>\r\n	<li>Нарушение свертываемости крови</li>\r\n	<li>Инфекционные заболевания</li>\r\n	<li>Беременность</li>\r\n	<li>Сахарный диабет</li>\r\n	<li>Наличие искусственного водителя ритма сердца</li>\r\n</ul>\r\n&nbsp;\r\n<h3><strong>Результат часто превосходит самые смелые ожидания пациентов</strong></h3>\r\n<ul class=\"check-list\">\r\n	<li>Уменьшаются отеки</li>\r\n	<li>Улучшается микроциркуляция</li>\r\n	<li>Нормализуется обмен веществ</li>\r\n	<li>Поврежденные ткани быстро восстанавливаются</li>\r\n	<li>Уходят болевые ощущения</li>\r\n	<li>Возвращается устойчивость к физическим нагрузкам</li>\r\n</ul>', 'Информация о сайте', '', 'inherit', 'closed', 'closed', '', '2-revision-v1', '', '', '2014-02-19 08:02:11', '2014-02-19 06:02:11', '', '2', 'http://384071.ortope00.web.hosting-test.net/2-revision-v1.html', '0', 'revision', '', '0' ); ";i:801;s:3988:"INSERT INTO rtpd_posts VALUES( '95', '2', '2014-02-19 08:02:47', '2014-02-19 06:02:47', '<h3><strong>Что мы лечим</strong></h3>\r\n<ul class=\"check-list\">\r\n	<li>Заболевания крупных суставов (тазобедренный, коленный, плечевой локтевой, лучезапясный, голеностопный) и мелких (кисть и стопа) — в том числе «теннисный локоть», «рука гольфиста»</li>\r\n	<li>Пяточную шпору и плантарный фасциит</li>\r\n	<li>Заболевания связок (тендопериостопатия и т. п.)</li>\r\n	<li>Мышечно-тонические боли при остеохондрозе позвоночника, кокцикодинию</li>\r\n	<li>Мускульные болевые (триггерные) точки</li>\r\n	<li>Замедленную консолидацию длинных трубчатых костей</li>\r\n</ul>\r\n<h3><strong>Принцип действия ударно-волновой терапии</strong></h3>\r\nУдарно-волновая терапия проводится на специальном аппарате — генераторе тока. Электрический импульс, подаваемый прибором, преобразуется в звуковую волну и проникает в тело человека. Встречая на своем пути ткани различной плотности, акустическая волна с частотой 1–10 Гц распространяется в теле человека и поглощается, достигнув кости. Энергии ударно-волнового воздействия достаточно для оздоровления фиброзных участков, костных разрастаний и зон отложения солей. Кроме того, создаются условия для более быстрого срастания костей при переломах.\r\n<h3><strong>Преимущества метода</strong></h3>\r\n<ul class=\"check-list\">\r\n	<li>УВТ хорошо переносится и не требует обезболивания</li>\r\n	<li>Проводится в амбулаторных условиях</li>\r\n	<li>Во многих случаях позволяет избежать проведения оперативного вмешательства</li>\r\n</ul>\r\n&nbsp;\r\n<h3><strong>Противопоказания</strong></h3>\r\n<ul class=\"check-list\">\r\n	<li>Онкологические заболевания</li>\r\n	<li>Нарушение свертываемости крови</li>\r\n	<li>Инфекционные заболевания</li>\r\n	<li>Беременность</li>\r\n	<li>Сахарный диабет</li>\r\n	<li>Наличие искусственного водителя ритма сердца</li>\r\n</ul>\r\n&nbsp;\r\n<h3><strong>Результат часто превосходит самые смелые ожидания пациентов</strong></h3>\r\n<ul class=\"check-list\">\r\n	<li>Уменьшаются отеки</li>\r\n	<li>Улучшается микроциркуляция</li>\r\n	<li>Нормализуется обмен веществ</li>\r\n	<li>Поврежденные ткани быстро восстанавливаются</li>\r\n	<li>Уходят болевые ощущения</li>\r\n	<li>Возвращается устойчивость к физическим нагрузкам</li>\r\n</ul>', 'Информация о сайте', '', 'inherit', 'closed', 'closed', '', '2-revision-v1', '', '', '2014-02-19 08:02:47', '2014-02-19 06:02:47', '', '2', 'http://384071.ortope00.web.hosting-test.net/2-revision-v1.html', '0', 'revision', '', '0'