a:1661:{i:0;s:39:"DROP TABLE IF EXISTS rtpd_Ihrss_plugin;";i:1;s:628:"CREATE TABLE `rtpd_Ihrss_plugin` (
  `Ihrss_id` int(11) NOT NULL AUTO_INCREMENT,
  `Ihrss_path` text CHARACTER SET utf8 COLLATE utf8_bin NOT NULL,
  `Ihrss_link` text CHARACTER SET utf8 COLLATE utf8_bin NOT NULL,
  `Ihrss_target` varchar(50) NOT NULL,
  `Ihrss_title` varchar(500) NOT NULL,
  `Ihrss_order` int(11) NOT NULL,
  `Ihrss_status` varchar(10) NOT NULL,
  `Ihrss_type` varchar(100) NOT NULL,
  `Ihrss_extra1` varchar(100) NOT NULL,
  `Ihrss_extra2` varchar(100) NOT NULL,
  `Ihrss_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  PRIMARY KEY (`Ihrss_id`)
) ENGINE=MyISAM AUTO_INCREMENT=12 DEFAULT CHARSET=utf8;";i:2;s:224:"INSERT INTO rtpd_Ihrss_plugin VALUES( '1', 'http://ortoped.lg.ua/wp-content/plugins/image-horizontal-reel-scroll-slideshow/images/Sing_1.jpg', '#', '_blank', 'Image 1', '1', 'YES', 'GROUP1', '', '', '0000-00-00 00:00:00' ); ";i:3;s:224:"INSERT INTO rtpd_Ihrss_plugin VALUES( '2', 'http://ortoped.lg.ua/wp-content/plugins/image-horizontal-reel-scroll-slideshow/images/Sing_2.jpg', '#', '_blank', 'Image 2', '2', 'YES', 'GROUP1', '', '', '0000-00-00 00:00:00' ); ";i:4;s:224:"INSERT INTO rtpd_Ihrss_plugin VALUES( '3', 'http://ortoped.lg.ua/wp-content/plugins/image-horizontal-reel-scroll-slideshow/images/Sing_3.jpg', '#', '_blank', 'Image 3', '0', 'YES', 'GROUP2', '', '', '0000-00-00 00:00:00' ); ";i:5;s:224:"INSERT INTO rtpd_Ihrss_plugin VALUES( '4', 'http://ortoped.lg.ua/wp-content/plugins/image-horizontal-reel-scroll-slideshow/images/Sing_4.jpg', '#', '_blank', 'Image 4', '1', 'YES', 'GROUP2', '', '', '0000-00-00 00:00:00' ); ";i:6;s:289:"INSERT INTO rtpd_Ihrss_plugin VALUES( '6', 'http://ortoped.lg.ua/wp-content/plugins/image-horizontal-reel-scroll-slideshow/images/1.jpg', 'http://ortoped.lg.ua/nuzhna-li-detyam-ortopedicheskaya-obuv.html', '_blank', 'hallux_valgus_1', '1', 'NO', 'Widget', '', '', '0000-00-00 00:00:00' ); ";i:7;s:281:"INSERT INTO rtpd_Ihrss_plugin VALUES( '7', 'http://ortoped.lg.ua/wp-content/plugins/image-horizontal-reel-scroll-slideshow/images/2.jpg', 'http://ortoped.lg.ua/nuzhna-li-detyam-ortopedicheskaya-obuv.html', '_blank', 'image_1', '2', 'NO', 'Widget', '', '', '0000-00-00 00:00:00' ); ";i:8;s:289:"INSERT INTO rtpd_Ihrss_plugin VALUES( '8', 'http://ortoped.lg.ua/wp-content/plugins/image-horizontal-reel-scroll-slideshow/images/3.jpg', 'http://ortoped.lg.ua/nuzhna-li-detyam-ortopedicheskaya-obuv.html', '_blank', 'hallux_valgus_3', '3', 'NO', 'Widget', '', '', '0000-00-00 00:00:00' ); ";i:9;s:226:"INSERT INTO rtpd_Ihrss_plugin VALUES( '9', 'http://ortoped.lg.ua/wp-content/plugins/image-horizontal-reel-scroll-slideshow/images/4.jpg', '#', '_blank', 'hallux_valgus_4', '4', 'NO', 'Widget', '', '', '0000-00-00 00:00:00' ); ";i:10;s:227:"INSERT INTO rtpd_Ihrss_plugin VALUES( '10', 'http://ortoped.lg.ua/wp-content/plugins/image-horizontal-reel-scroll-slideshow/images/5.jpg', '#', '_blank', 'hallux_valgus_5', '6', 'NO', 'Widget', '', '', '0000-00-00 00:00:00' ); ";i:11;s:227:"INSERT INTO rtpd_Ihrss_plugin VALUES( '11', 'http://ortoped.lg.ua/wp-content/plugins/image-horizontal-reel-scroll-slideshow/images/6.jpg', '#', '_blank', 'hallux_valgus_6', '5', 'NO', 'Widget', '', '', '0000-00-00 00:00:00' ); ";i:12;s:40:"DROP TABLE IF EXISTS rtpd_bwps_lockouts;";i:13;s:319:"CREATE TABLE `rtpd_bwps_lockouts` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `type` int(1) NOT NULL,
  `active` int(1) NOT NULL,
  `starttime` int(10) NOT NULL,
  `exptime` int(10) NOT NULL,
  `host` varchar(20) DEFAULT NULL,
  `user` bigint(20) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;";i:14;s:35:"DROP TABLE IF EXISTS rtpd_bwps_log;";i:15;s:432:"CREATE TABLE `rtpd_bwps_log` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `type` int(1) NOT NULL,
  `timestamp` int(10) NOT NULL,
  `host` varchar(20) DEFAULT NULL,
  `user` bigint(20) DEFAULT NULL,
  `username` varchar(255) DEFAULT NULL,
  `url` varchar(255) DEFAULT NULL,
  `mem_used` varchar(255) DEFAULT NULL,
  `referrer` varchar(255) DEFAULT NULL,
  `data` mediumtext,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;";i:16;s:38:"DROP TABLE IF EXISTS rtpd_commentmeta;";i:17;s:361:"CREATE TABLE `rtpd_commentmeta` (
  `meta_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `comment_id` bigint(20) unsigned NOT NULL DEFAULT '0',
  `meta_key` varchar(255) DEFAULT NULL,
  `meta_value` longtext,
  PRIMARY KEY (`meta_id`),
  KEY `comment_id` (`comment_id`),
  KEY `meta_key` (`meta_key`)
) ENGINE=MyISAM AUTO_INCREMENT=261 DEFAULT CHARSET=utf8;";i:18;s:83:"INSERT INTO rtpd_commentmeta VALUES( '214', '140', '_wp_trash_meta_status', '0' ); ";i:19;s:35:"DROP TABLE IF EXISTS rtpd_comments;";i:20;s:1149:"CREATE TABLE `rtpd_comments` (
  `comment_ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `comment_post_ID` bigint(20) unsigned NOT NULL DEFAULT '0',
  `comment_author` tinytext NOT NULL,
  `comment_author_email` varchar(100) NOT NULL DEFAULT '',
  `comment_author_url` varchar(200) NOT NULL DEFAULT '',
  `comment_author_IP` varchar(100) NOT NULL DEFAULT '',
  `comment_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `comment_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `comment_content` text NOT NULL,
  `comment_karma` int(11) NOT NULL DEFAULT '0',
  `comment_approved` varchar(20) NOT NULL DEFAULT '1',
  `comment_agent` varchar(255) NOT NULL DEFAULT '',
  `comment_type` varchar(20) NOT NULL DEFAULT '',
  `comment_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `user_id` bigint(20) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`comment_ID`),
  KEY `comment_post_ID` (`comment_post_ID`),
  KEY `comment_approved_date_gmt` (`comment_approved`,`comment_date_gmt`),
  KEY `comment_date_gmt` (`comment_date_gmt`),
  KEY `comment_parent` (`comment_parent`)
) ENGINE=MyISAM AUTO_INCREMENT=178 DEFAULT CHARSET=utf8;";i:21;s:850:"INSERT INTO rtpd_comments VALUES( '7', '142', 'Александр Хмаренко', 'a.hmarenko@yandex.ru', 'http://ortoped.lg.ua', '185.37.194.98', '2014-04-19 18:10:11', '2014-04-19 16:10:11', 'Здравствуйте, Виктория. Предоперационное планирование будет учитывать разную степень деформаций и, соответственно, после вмешательства стопы не будут сильно отличаться. Одновременно можно оперировать обе стопы. Для более полного ответа пришлите мне на ящик фото своих рентгенов.\r\nС уважением, А.Н.', '0', '1', 'Mozilla/5.0 (compatible; MSIE 10.0; Windows NT 6.1; WOW64; Trident/6.0)', '', '6', '3' ); ";i:22;s:812:"INSERT INTO rtpd_comments VALUES( '6', '142', 'Victoria', 'sasha750@ya.ru', '', '185.37.194.98', '2014-04-18 15:12:27', '2014-04-18 13:12:27', 'Здравствуйте, Александр Николаевич. Мне поставили диагноз двустороннее поперечное плоскостопие, вальгусная деформация первых пальцев 2-3 ст. При этом беспокоят обе стопы, хотя слева меньше. Можно ли делать одновременно операцию на двух ногах и будут ли они отличаться после операции (сейчас отличие заметно)?', '0', '1', 'Mozilla/5.0 (compatible; MSIE 10.0; Windows NT 6.1; WOW64; Trident/6.0)', '', '0', '0' ); ";i:23;s:961:"INSERT INTO rtpd_comments VALUES( '8', '142', 'Александра Петровна', 'proncheva@mail.ru', '', '185.37.194.98', '2014-04-20 15:26:18', '2014-04-20 13:26:18', 'Здравствуйте, Александр Николаевич. Суть моей проблемы — мозоль в области второго пальца на ступне. Большая проблема с подбор новой обуви. И чем дальше, тем хуже. Сам палец не до конца разгибается. Как я поняла — это можно лечить без операции или как-то так. Насколько это возможно без госпитализации? Какая реабилитация? Есть методы эффективного уменьшения самой мозоли?\r\n', '0', '1', 'Mozilla/5.0 (compatible; MSIE 10.0; Windows NT 6.1; WOW64; Trident/6.0)', '', '0', '0' ); ";i:24;s:1342:"INSERT INTO rtpd_comments VALUES( '10', '142', 'Александр Хмаренко', 'a.hmarenko@yandex.ru', 'http://ortoped.lg.ua', '185.37.194.98', '2014-04-20 18:13:34', '2014-04-20 16:13:34', 'Здравствуйте, Александра Петровна. Насколько я понял, речь идет о «молоткообразной» деформации 2-го пальца. Появление болезненной, а иногда очень болезненной мозоли, связано с фиксированной контрактурой (тугоподвижностью в положении сгибания) межфалангового сустава пальца. Именно поэтому все методы наружного воздействия на саму мозоль неэффективны. Но возможно провести малоинвазивную операцию из микропрокола кожи под местной анестезией. Госпитализация не нужна. Но это все предварительно. Пришлите на мой ящик a.hmarenko@ya.ru фото стоп и рентгенов.\r\nС уважением, А.Н.', '0', '1', 'Mozilla/5.0 (compatible; MSIE 10.0; Windows NT 6.1; WOW64; Trident/6.0)', '', '8', '3' ); ";i:25;s:1214:"INSERT INTO rtpd_comments VALUES( '20', '142', 'Владимир', 'vpolos_@ya.ru', '', '185.37.194.169', '2014-04-29 16:05:08', '2014-04-29 14:05:08', 'Здравствуйте, Александр Николаевич. У моей мамы большие деформации стоп. По словам нашего хирурга — это халюс вальгус и вряд ли ей поможет операция. При этом косточки возле больших пальцев ее беспокоят меньше всего. Самый большой дискомфорт вызывают мозоли на втором и третьем пальцах и натоптыши на подошвах. Регулярно пользуется стельками, но толку от них мало. Вопросы: сколько будет стоить операция, можно ли ее делать 70-летней и когда можно будет самостоятельно передвигаться?', '0', '1', 'Mozilla/5.0 (Linux; Android 4.3; ME302C Build/JSS15Q) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/34.0.1847.116 Safari/537.36 OPR/21.0.1437.74904', '', '0', '0' ); ";i:26;s:1209:"INSERT INTO rtpd_comments VALUES( '12', '142', 'Ирина В.', 'Brats@mail.ru', '', '185.37.195.29', '2014-04-23 11:54:27', '2014-04-23 09:54:27', 'Добрый вечер. Ортопед мне поставил диагноз халюс вальгус обеих стоп 2 степени и посоветовал ванночки с морской солью, супинаторы, массаж. Я прошла все эти процедуры. Не могу сказать, что бесполезно — боли стали меньше, но как только надеваю любую обувь, кроме кроссовок и тапок, сразу же испытываю боль в области «шишек» и суставов больших пальцев. Насколько возможна операция сразу на двух стопах и как быстро я смогу быть дома? И вообще, когда можно вернуться к полноценной жизни?\r\n', '0', '1', 'Mozilla/5.0 (Linux; Android 4.3; ME302C Build/JSS15Q) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/33.0.1750.166 Safari/537.36 OPR/20.0.1396.73172', '', '0', '0' ); ";i:27;s:1247:"INSERT INTO rtpd_comments VALUES( '13', '142', 'Александр Хмаренко', 'a.hmarenko@yandex.ru', 'http://ortoped.lg.ua', '185.37.195.29', '2014-04-23 20:47:17', '2014-04-23 18:47:17', 'Здравствуйте, Ирина. Операция на двух стопах в последнее время стала быстрее правилом, чем исключением, т. к. при одинаковом времени реабилитации — 2 месяца, все проходит под одним наркозом, Вы получаете ту же дозу медикаментов, пользуетесь одной парой туфель Барука, ну и моральные и финансовые затраты одномоментного вмешательства на обеих стопах не сравнить с двумя этапами, что немаловажно. Домой пациенты, кстати, отправляются, как правило, на 2–3 сутки.\r\nС уважением, А.Н.', '0', '1', 'Mozilla/5.0 (Linux; Android 4.3; ME302C Build/JSS15Q) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/33.0.1750.166 Safari/537.36 OPR/20.0.1396.73172', '', '12', '3' ); ";i:28;s:1290:"INSERT INTO rtpd_comments VALUES( '14', '142', 'Елена Федоровна', 'ivanchenko_@ya.ua', 'http://ortoped.lg.ua', '185.37.193.243', '2014-04-24 20:24:40', '2014-04-24 18:24:40', 'Здравствуйте, Александр Николаевич. У меня в течение 5 лет появились болезненные натоптыши под подушечками второго и третьего пальцев стоп. Педикюр помогает, но ненадолго. Пользуюсь стельками с валиками от поперечного плоскостопия. В них легче, но опять же таки лишь отчасти. Прочитала на Вашем сайте инфу «О мозолях, натоптышах и „молотках“». Скажите, это касается моего случая — я имею в виду возможность операции без разреза и госпитализации или я что-то не так поняла?\r\nС уважением, Елена Федоровна.', '0', '1', 'Mozilla/5.0 (Linux; Android 4.3; ME302C Build/JSS15Q) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/34.0.1847.116 Safari/537.36 OPR/21.0.1437.74904', '', '0', '3' ); ";i:29;s:1695:"INSERT INTO rtpd_comments VALUES( '15', '142', 'Александр Хмаренко', 'a.hmarenko@yandex.ru', 'http://ortoped.lg.ua', '185.37.193.243', '2014-04-25 18:05:00', '2014-04-25 16:05:00', 'Здравствуйте, Елена Федоровна. Описанные Вами натоптыши обычно появляются при поперечном плоскостопии. Часто бывает дотаточо пользоваться стельками с валиком типа Зейца — примерно на границе средней и передней 1/3 стопы, отказаться от модельной обуви и наблюдается регресс симптоматики. Но в далеко зашедших случаях этого мало. Малоинвазивная хирургия стоп для лечения причины образования натоптышей — не сложная малоинвазивная операция, выполняемая под местной анестезией, в амбулаторных условиях. Нужно сделать рентгеновские снимки в прямой проекции и по касательной к поперечному своду стоп, записаться ко мне на консультацию, где мы с Вами и решим, что в конкретном случае нужно предпринять.\r\nС уважением, А.Н.', '0', '1', 'Mozilla/5.0 (Linux; Android 4.3; ME302C Build/JSS15Q) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/34.0.1847.116 Safari/537.36 OPR/21.0.1437.74904', '', '14', '3' ); ";i:30;s:910:"INSERT INTO rtpd_comments VALUES( '16', '142', 'Дмитрий Ш.', 'dmishun@ya.ru', '', '31.40.179.241', '2014-04-27 10:43:29', '2014-04-27 08:43:29', 'Моему сыну 5 лет. Он стал жаловаться на боли в ногах после нагрузки. Хирург ему поставил диагноз плосковальгусная деформация стоп и назначил массаж и стельки-супинаторы для постоянного ношения. Как к Вам записаться на прием и что нужно для того, чтобы заказать индивидуальные ортопедические стельки?\r\nДмитрий', '0', '1', 'Mozilla/5.0 (Linux; Android 4.3; LT25i Build/9.2.A.0.295) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/29.0.1547.72 Mobile Safari/537.36 OPR/16.0.1212.64462', '', '0', '0' ); ";i:31;s:809:"INSERT INTO rtpd_comments VALUES( '17', '142', 'Александр Хмаренко', 'a.hmarenko@yandex.ru', 'http://ortoped.lg.ua', '31.40.179.241', '2014-04-27 10:57:54', '2014-04-27 08:57:54', 'Здравствуйте, Дмитрий. Записаться на прием можно по телефону 0955058547. Во время осмотра определимся с тем, нуждается ли Ваш сын в ортопедических стельках. Если да, то уточним размер и изготовим, что занимает около 30 минут.\r\nС уважением, А.Н.', '0', '1', 'Mozilla/5.0 (Linux; Android 4.3; ME302C Build/JSS15Q) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/34.0.1847.116 Safari/537.36 OPR/21.0.1437.74904', '', '16', '3' ); ";i:32;s:1229:"INSERT INTO rtpd_comments VALUES( '18', '142', 'Татьяна Владимировна', 'Tatden@ya.ru', '', '31.40.179.241', '2014-04-26 18:15:06', '2014-04-26 16:15:06', 'Здравствуйте, Александр Николаевич. Меня на протяжении трёх месяцев беспокоит боль в пятке. Она не постоянная: очень сильная во время первых шагов после ночи, уменьшается в течение 5–10 минут и усиливается во второй половине дня. Травматолог сказал, что это пяточная шпора и направил меня на физиолечение/УВЧ, ультразвук. Прошла 10 процедур, а результата нет. Прочитала на Вашем сайте об ударно-волновой терапии. Как это происходит, сколько нужно сеансов и что для этого нужно?', '0', '1', 'Mozilla/5.0 (Linux; Android 4.3; LT25i Build/9.2.A.0.295) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/29.0.1547.72 Mobile Safari/537.36 OPR/16.0.1212.64462', '', '0', '0' ); ";i:33;s:917:"INSERT INTO rtpd_comments VALUES( '19', '142', 'Александр Хмаренко', 'a.hmarenko@yandex.ru', 'http://ortoped.lg.ua', '31.40.179.241', '2014-04-27 11:57:59', '2014-04-27 09:57:59', 'Здравствуйте, Татьяна Владимировна. Процедура УВТ (ударно-волновая терапия) проходит амбулаторно. При себе желательно иметь рентгеновский снимок пяточной кости в боковой проекции, если нет, то можно сделать у меня. Сеансы проводятся 1 раз в неделю. Курс составляет 4–5 сеансов.\r\nС уважением, А.Н.', '0', '1', 'Mozilla/5.0 (Linux; Android 4.3; ME302C Build/JSS15Q) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/34.0.1847.116 Safari/537.36 OPR/21.0.1437.74904', '', '18', '3' ); ";i:34;s:1206:"INSERT INTO rtpd_comments VALUES( '21', '142', 'Александр Хмаренко', 'a.hmarenko@yandex.ru', 'http://ortoped.lg.ua', '185.37.193.124', '2014-04-30 18:35:44', '2014-04-30 16:35:44', 'Здравствуйте, Владимир. Возраст как таковой не является противопоказанием для операции. Нужно пройти несложное обследование (см. <a href=\"http://ortoped.lg.ua/faqs.html\" rel=\"nofollow\">«Часто задаваемые Вопросы и Ответы»</a>) и, если серьезных отклонений нет, то можно снять этот вопрос. Стоимость зависит от сложности деформаций, количества имплантов (винты Герберта) и медикаментозного сопровождения. Ориентировочно от 400 у.е. Самостоятельно ходить можно на второй день после операции. тел.- 0955058547.\r\nС уважением, А.Н.', '0', '1', 'Mozilla/5.0 (compatible; MSIE 10.0; Windows NT 6.1; WOW64; Trident/6.0)', '', '20', '3' ); ";i:35;s:1621:"INSERT INTO rtpd_comments VALUES( '45', '142', 'Sergey.A', 'ssirchenko1@gmail.com', '', '93.77.114.155', '2014-09-21 08:08:46', '2014-09-21 06:08:46', 'Уважаемый Александр Николаевич\r\nУзнал Ваш адрес из интернет источников. Благодарные отзывы Ваших пациентов внушают доверие и говорят о Вас как об отличном мастере своего дела и прекрасном человеке.\r\nХотел бы спросить Вас про возможность проведении операции по устранению \"косточек\" на ногах используя современные методы малотравматичного способа коррекции деформации первого пальца. Делаете ли Вы такие операции? Вопрос цены также очень актуальный, т.к. необходимо понимать свою готовность и возможности. Пациент: женщина, 57лет. По состоянию здоровья передвижение затруднено, поэтому первичные вопросы задаю по интернет. Фото ног в приложении. Заранее благодарен за внимание\r\nС уважением, Сергей С.\r\nХарьков', '0', '1', 'Mozilla/5.0 (Linux; Android 4.3; ME302C Build/JSS15Q) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/37.0.2062.117 Safari/537.36', '', '0', '0' ); ";i:36;s:903:"INSERT INTO rtpd_comments VALUES( '46', '142', 'Александр Хмаренко', 'a.hmarenko@yandex.ru', 'http://ortoped.lg.ua', '82.145.216.178', '2014-09-21 08:19:11', '2014-09-21 06:19:11', 'Здравствуйте, Сергей. Такие операции я делаю в клинике \"Доктор Алекс\". Стоимость можно посмотреть на сайте в подразделе \"хирургия стопы\" http://www.doctor-alex.ua/price/ . Думаю, что категория сложности будет первая или вторая(не сложная). Приемные дни вторник и пятница. Запись на приём по телефону клиники или 0955058547.\r\nС уважением А.Н.', '0', '1', 'Opera/9.80 (Android; Opera Mini/24.0.1565/35.4530; U; ru) Presto/2.8.119 Version/11.10', '', '45', '3' ); ";i:37;s:4644:"INSERT INTO rtpd_comments VALUES( '47', '142', 'Алина Носуля', 'alusha25@inbox.ru', 'http://vk.com/id67002384', '31.40.179.239', '2014-09-23 21:10:46', '2014-09-23 19:10:46', 'Здравствуйте! Хочу поделиться своей больничной историей, о которой остались, как ни странно, хорошие воспоминания. Все потому, что я попала к нужному ,надежному и хорошему доктору - Александру Николаевичу! В феврале 2013 г., в свои 18 на тот момент, я пришла на консультацию. Меня беспокоила тянущая, ноющая боль в стопах. Временами боль доходила до коленей. Так же отмечалась боль в пятках. Но это все я списывала на усталость, пока я не начала замечать изменения в обуви. На то время была еще зима, и я просто не могла уже ходить в своих сапогах, т.к. они очень сильно стоптались вовнутрь и это было уже очень заметно. Для меня это стало сигналом обратится к доктору и диагноз был таков: \"двухстороннее плоскостопие, вальгусная деформация обеих стоп\", т.е. мои стопы были искривлены вовнутрь на 10 градусов.  Как сказал Александр Николаевич , ортопедические стельки уже не помогли бы, и нужно было делать операцию. 26 июня меня прооперировали, а именно провели \"подтаранный артропоэрез правой и левой стоп винтом Vilex\". Это современный метод и я всем его советую! Александр Николаевич конечно сделал все мастерски, могу сказать ювелирная работа! После операции, ноги по колено забинтовали эластичным бинтом и поместили в гипсовые шины. Так я пролежала неделю, после чего мы их сняли и я стала на свои \"новые стопы\". Ощущения были непередаваемые...да было непривычно, да было очень больно...но это того стоило. Еще через недельку уже сняли швы , сейчас спустя год следов от них даже не заметно. В общей сложности за месяц я полностью расходилась. Как же было приятно смотреть на свои ровные стопы)) Кстати ,после операции я ходила в обуви с жесткой пяткой и с ортопедическими стельками, с ними нужно было проходить год после операции. Я спортсменка по жизни, и мне было важно, как же я буду в дальнейшем заниматься? Но сейчас я спокойно занимаюсь спортом во всю...я не чувствую ни какой боли, мои ноги ровные и красивые! В общем это круто, я рада что познакомилась с таким замечательным доктором и человеком, Александром Николаевичем! Я очень благодарна ему. Как я говорю у себя в семье \" этот человек поставил меня на ноги в прямом смысле слова)\". P.S. Когда спустя месяц после операции я поехала в августе на море, первое что я сделала, это побежала на пляж и поставила отпечатки своих новых стоп, уже не таких, как были \"ласты\", а красивых и с \"выемками\"...этой радости действительно не передать!', '0', '1', 'Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/37.0.2062.120 Safari/537.36', '', '0', '4' ); ";i:38;s:1369:"INSERT INTO rtpd_comments VALUES( '134', '142', 'Александр Хмаренко', 'a.hmarenko@gmail.com', 'http://ortoped.lg.ua', '185.37.194.217', '2015-01-20 18:57:28', '2015-01-20 16:57:28', 'Здравствуйте. Стоимость операции зависит от сложности и количества имплантов(от 4000 грн). Более точно можно посмотреть на сайте \"Доктор Алекс\" в подразделе \"хирургия стопы\" по ссылке http://www.doctor-alex.ua/price/. Реабилитация проходит параллельно с заживлением ран: со вторых суток пациент самостоятельно ходит в туфлях-Барука, а через два-три дня выписывается домой. Пальцами можно и нужно работать все это время, т.е. они не фиксируются. Спустя две недели снимаются швы и разрешается принимать душ и ванну. На обычную обувь переходим через 1 месяц. Вот, где-то так. Тел.- 0955058547\r\nС уважением, А.Н.', '0', '1', 'Mozilla/5.0 (compatible; MSIE 10.0; Windows NT 6.1; WOW64; Trident/6.0)', '', '125', '2' ); ";i:39;s:1424:"INSERT INTO rtpd_comments VALUES( '54', '142', 'нелли', 'nellysolovyova@rambler.ru', '', '77.52.68.32', '2014-09-28 15:17:55', '2014-09-28 13:17:55', 'В авгесте 2013 года мне делали операцию по коррекции деформации пальцев ног (так называемые \"косточки\").\r\nНа следующий день я уже могла ходить, и скаждым днем мой маршрут становился продолжительнее.\r\nДля меня самое главное - возможность ходить летом в открытой изящной обуви, не пряча ноги в закрытые туфли, и не страдать от боли.\r\nЯ благодарна своему хирургу, Хмаренко Александру Николаевичу, за его профессионализм, чуткость, любовь к своей работе.На его счету множество подобных операций. После общения с ним и его пациентами у меня не возникло сомнений делать или нет. Он мой ангел-хранитель.\r\nНе бойтесь таких операций и будьте красивыми!', '0', '1', 'Opera/9.80 (Windows NT 5.1; U; ru) Presto/2.10.289 Version/12.01', '', '0', '0' ); ";i:40;s:2495:"INSERT INTO rtpd_comments VALUES( '55', '142', 'Богданова Н.А.', 'aleksa.scorp@yandex.ru', '', '31.23.231.205', '2014-09-28 16:42:33', '2014-09-28 14:42:33', 'Здравствуйте! Хочу рассказать свои впечатления, которые остались у меня после оперативного лечения вальгусной деформации больших пальцев обеих стоп. В ноябре прошлого года прошла консультацию у Александра Николаевича, после чего он мне назначил операцию по коррекции стоп с исправлением поперечной формы плоскостопия. Процедура заняла два часа, используя совмещение местного и общего наркоза. После суток, проведенных в постели, меня обули в специальные туфли Барука, ноги после операции были забинтованы по колено. Ходить училась несколько дней. В клинике провела трое суток. Швы сняли через две недели. Болевой синдром присутствовал в течение первых шести дней и когда много походишь. Туфли носила полтора месяца. Сейчас прошло больше полугода, результатом очень довольна: изменилась походка, исчезла небольшая косолапость, перестала болеть поясница после длительной ходьбы и больше не подворачиваются ноги. Внешне остались еле заметные два шва на каждой стопе, сбоку – с внутренней стороны и сверху. На сегодняшний день комфортно себя чувствую в обуви на каблуке 4-6 см, ноги не устают и не болят, а форма стоп просто идеальна. Большое спасибо талантливому специалисту, доктору Хмаренко А.Н.', '0', '1', 'Mozilla/5.0 (Windows NT 6.1) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/37.0.2062.124 Safari/537.36', '', '0', '0' ); ";i:41;s:637:"INSERT INTO rtpd_comments VALUES( '125', '142', 'Анна', 'blokhinaa@ukr.net', '', '178.150.187.238', '2015-01-12 13:48:43', '2015-01-12 11:48:43', 'Здравствуйте, Александр Николаевич!!! \r\nУ моей мамы (58 лет) Сильнейшие боли в ногах - растут шишки на больших пальцах. Подскажите сколько стоит операция. Какой период реабилитации??', '0', '1', 'Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/30.0.1599.101 Safari/537.36 OPR/17.0.1241.53', '', '0', '0' ); ";i:42;s:1771:"INSERT INTO rtpd_comments VALUES( '90', '138', 'Фаина', 'farina.safarova@mail.ru', '', '95.153.171.187', '2014-11-04 09:49:34', '2014-11-04 07:49:34', 'ЗДравствуйте. Я хотела узнать  можно ли применять ударно-волновую терапию для разрушение костной мозоли. Меня прооперировали 10 сентября и в руку вставили 2 пластины. 1 сентября сломала руку, открытый оскольчатый перелом нижней трети плечевой кости (чрезмышелковый). Через месяц  после операции сделали снимок, на снимке появилась костная мазоль над местом перелома, она мешает при разработке руки, рука костенеет быстро, сильные боли при сгибании. Не знаю что мне делать.(((Помогите пож-та. Врач сказал,что поможет ударно-волновая терапия, но он не знает можно ли мне ее, так как не работал с такой терапией. (Хронических заболеваний у меня нет, мне 17.)Боюсь, что придется пооперироваться повторно для удаления мазоли и рука потеряет функцию. Посоветуйте пож-та что мне делать в данной ситуации.Спасибо заранее.', '0', '0', 'Mozilla/5.0 (Windows NT 6.2; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/38.0.2125.111 Safari/537.36', '', '0', '0' ); ";i:43;s:1291:"INSERT INTO rtpd_comments VALUES( '121', '142', 'Ольга Чугреева', 'olga4v@mail.ru', 'http://my.mail.ru/mail/olga4v/', '178.216.122.247', '2015-01-10 22:23:14', '2015-01-10 20:23:14', 'Уважаемый Александр Николаевич!  Вы оперировали мою дочь Наталью из Макеевки по поподу вальгусной деформации пальцев и искривления подтаранного сустава(подтаранный артроэрез), оперировали в мае и июне, 2012 года,  а осенью того же года  на больших пальцах появилась синева и ногти постепенно отвалились частично, выросли новые, но с периодичностью раз в пол года все повторяется, в кождиспансере  сдали анализ на грибок- ответ отрицательный.Могут ли быть после операции проблемы с ногтями больших пальцев?Что с этим делать ?Заранее благодарю за ответ.', '0', '1', 'Opera/9.80 (Windows NT 5.1) Presto/2.12.388 Version/12.12', '', '0', '5' ); ";i:44;s:561:"INSERT INTO rtpd_comments VALUES( '140', '35', 'RandallSl', 'myfkulas@gmail.com', '', '95.79.80.127', '2015-01-27 21:05:10', '2015-01-27 19:05:10', 'viagra and bph\r\n [url=http://genericviagraonline-usa.com]viagra no prescription\r\n[/url]  better than viagra\r\n <a href=\"http://genericviagraonline-usa.com\" rel=\"nofollow\">generic viagra usa\r\n</a> - info viagra\r\n viagra 10 mg 4 tablet display posts from previous', '0', 'spam', 'Mozilla/5.0 (Windows NT 6.1) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/37.0.2062.124 Safari/537.36', '', '0', '0' ); ";i:45;s:2199:"INSERT INTO rtpd_comments VALUES( '68', '142', 'Наталия', 'perfect_natysia@mail.ru', '', '178.216.122.247', '2014-10-07 19:30:44', '2014-10-07 17:30:44', 'В 19 лет я столкнулась с огромной проблемой из-за так называемых \"косточек\" - это было настоящее хождение по мукам:постоянные боли в обуви и без нее, невозможность нормально ходить,но самое печальное было то,что врачи разводили руками и советовали носить стельки и делать ванночки,хотя толку от этого никакого не было и быть не могло. Дело спасения утопающих-дело рук самих утопающих, я стала искать пути решения свое проблемы в интернете и нашла информацию о докторе Хмаренко Александре Николаевиче,которой стал моей последней надеждой. После первой консультации возникла уверенность в профессионализме Александра Николаевича - он прооперировал мне обе ступни по поводу вальгусной деформации и искривлению подтаранного сустава. На следующий день после операции я уже ходила в туфлях Барука (сначала по чуть-чуть,но с каждым днем все больше и больше). Через 2 недели мне сняли швы, а через месяц я уже ходила в обычной обуви.Вот уже 2 года я живу без мучений и очень благодарна Александру Николаевичу за то, что вернул меня к здоровой жизни.', '0', '1', 'Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/535.1 (KHTML, like Gecko) Chrome/14.0.835.163 Safari/535.1', '', '0', '0' ); ";i:46;s:2909:"INSERT INTO rtpd_comments VALUES( '161', '142', 'Victorwrex', 'freeboard@wegas.ru', 'http://freeboard.kz/', '46.119.63.74', '2015-02-19 20:46:17', '2015-02-19 18:46:17', '[url=http://freeboard.kz/c203-943.html]форевер ливинг продактс официальный сайт казахстан[/url]\r\n \r\nПоиск работы и необходимой услуги, покупка техники или домашнего питомца, приобретение недвижимого имущества – объявления на эти темы публикуются на специальных досках, где располагается любая информация по разным вопросам. \r\n \r\n[url=http://freeboard.kz/c184-2260.html]работа в алматы водитель сегодня срочно требуется[/url]\r\n \r\nРесурс http://freeboard.kz/ представляет собой такую интернет-доску, где вы можете не просто отыскать не только необходимый товар или специалиста для ремонта дачи или инсталляции кондиционера в помещении, но и поставить свое объявление на любую тему. Интернет-сайт имеет, в большинстве своём, данные, которыми могут воспользоваться жители Казахстана, но этим не ограничивается деятельность вебресурса. Вы легко сможете отыскать на сайте вакансии и на территории СНГ, сервисы, которые предлагаются в столице России и других крупных городах РФ. \r\n \r\n[url=http://freeboard.kz/c76-3706.html]протеиновый коктейль с кальцием nutrilite отзывы[/url]\r\n \r\nКомфорт такого ресурса давно оценили пользователи Интернета. В любое время суток вы сможете зайти и найти необходимую вам информацию. Также моментально вы получите сообщение об ответах на ваше поставленное объявление. Интернет сейчас имеется, практически, в любом доме, поэтому такое размещение данных о продаже и покупке, строительстве и учебе быстрей дойдет до потребителей, чем объявление в какой-либо газете.', '0', '0', 'Mozilla/5.0 (Windows NT 6.3) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/39.0.2171.71 Safari/537.36', '', '0', '0' ); ";i:47;s:990:"INSERT INTO rtpd_comments VALUES( '133', '142', 'Александр Хмаренко', 'a.hmarenko@gmail.com', 'http://ortoped.lg.ua', '185.37.194.217', '2015-01-20 18:18:27', '2015-01-20 16:18:27', 'Здравствуйте.Подобная проблема не должна быть связанной с операцией, которая не затрагивала ни ногтевое ложе, ни фаланги, ни пальцевые нервы. Возможно это какой-то вид грибка, который не смогли обнаружить. Попробуйте провести пробную терапию одним из противогрибковых препаратов после отхождения ногтевой пластинки. Например, \"ламизил-спрей\". Тел.- 0955058547\r\nС уважением, А.Н.', '0', '1', 'Mozilla/5.0 (compatible; MSIE 10.0; Windows NT 6.1; WOW64; Trident/6.0)', '', '121', '2' ); ";i:48;s:607:"INSERT INTO rtpd_comments VALUES( '102', '39', 'CharlieObelotef', 'mishinskiss@mail.ru', 'http://www.progon-sayta.ru/', '178.123.210.94', '2014-11-25 20:32:57', '2014-11-25 18:32:57', 'Здравствуйте, предлагаем Вам услуги комплексного продвижения вашего сайта, подробнее с нашими услугами Вы можете ознакомиться на нашем сайте http://www.anacron.ru/ Извините за беспокойство.', '0', '0', 'Opera/9.80 (Windows NT 5.1) Presto/2.12.388 Version/12